/// @title Marketplace
pragma solidity ^0.8.13;

import {ICartesiDAppFactory} from "@cartesi/rollups/contracts/dapp/ICartesiDAppFactory.sol";
import {IConsensus} from "@cartesi/rollups/contracts/consensus/IConsensus.sol";
import {ENS} from "@ensdomains/ens-contracts/contracts/registry/ENS.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {IMarketplace} from "./IMarketplace.sol";
import {IReaderNodeProvider, IValidatorNodeProvider} from "../provider/INodeProvider.sol";
import {IVault} from "../payment/IVault.sol";
import {ReaderNodeProvider, ValidatorNodeProvider} from "../provider/NodeProvider.sol";
import {Vault} from "../payment/Vault.sol";

/// @notice Factory for creating new ERC20 based providers
contract Marketplace is IMarketplace {
    ENS public immutable ens;
    ICartesiDAppFactory public immutable factory;

    constructor(ENS _ens, ICartesiDAppFactory _factory) {
        ens = _ens;
        factory = _factory;
    }

    /// @notice Create a new ERC20 based ValidatorNodeProvider using the specified token
    function newValidatorNodeProvider(
        IConsensus _consensus,
        IERC20 _token,
        address _payee,
        uint256 _price
    ) external returns (IValidatorNodeProvider) {
        address owner = msg.sender;

        // create factory using that token
        ValidatorNodeProvider provider = new ValidatorNodeProvider(
            owner,
            ens,
            _token,
            _payee,
            _price,
            factory,
            _consensus
        );

        // emit event
        emit ValidatorNodeProviderCreated(
            provider,
            _consensus,
            _token,
            _payee,
            _price
        );

        return provider;
    }

    /// @notice Create a new ERC20 based ReaderNodeProvider using the specified token
    function newReaderNodeProvider(
        IERC20 _token,
        address _payee,
        uint256 _price
    ) external returns (IReaderNodeProvider) {
        address owner = msg.sender;

        // create provider using that token
        IReaderNodeProvider provider = new ReaderNodeProvider(
            owner,
            ens,
            _token,
            _payee,
            _price
        );

        // emit event
        emit ReaderNodeProviderCreated(provider, _token, _payee, _price);

        return provider;
    }
}
","keccak256":"0xf3d8c5a6eb3befafedaed2d0ef7349521d9221bef210d4be2b499e229cb515ff","license":"Apache-2.0"},"contracts/payment/IVault.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
/// @title Vault interface
pragma solidity ^0.8.13;

import {ICartesiDApp} from "@cartesi/rollups/contracts/dapp/ICartesiDApp.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @notice Vault interface that adds a payment specification for the execution of application nodes
/// @dev The implementation should specify a token as payment method, and calculate the price for a
/// specified amount of execution time of the application node.
interface IVault {
    // Permissionless functions

    /// @notice Returns the cost of running an application for a given time
    /// @param _time The time in seconds
    /// @return The cost in amount of ERC-20 tokens
    function cost(uint256 _time) external view returns (uint256);

    /// @notice The token used by the vault
    function token() external view returns (IERC20);

    /// @notice The address which will receive payments for the application node execution
    function payee() external view returns (address);

    /// @notice Returns the runway of a given application
    /// @dev Runway is the amount of time (in seconds) an application node has funds to run
    /// @param _application The application address
    function runway(ICartesiDApp _application) external view returns (uint256);

    /// @notice Extend the runway of a given application by an amount of time
    /// @dev The user must approve the token transfer from the caller to the payee beforehand.
    /// @param _application The application
    /// @param _time The amount of time to extend the runway (will trigger transfer)
    function extendRunway(
        ICartesiDApp _application,
        uint256 _time
    ) external returns (uint256);
}
